        // static
        bool MediaImage_Data::Validate(
            const void* data,
            mojo::internal::ValidationContext* validation_context)
        {
            if (!data)
                return true;

            if (!ValidateStructHeaderAndClaimMemory(data, validation_context))
                return false;

            // NOTE: The memory backing |object| may be smaller than |sizeof(*object)| if
            // the message comes from an older version.
            const MediaImage_Data* object = static_cast<const MediaImage_Data*>(data);

            static constexpr struct {
                uint32_t version;
                uint32_t num_bytes;
            } kVersionSizes[] = { { 0, 32 } };

            if (object->header_.version <= kVersionSizes[arraysize(kVersionSizes) - 1].version) {
                // Scan in reverse order to optimize for more recent versions.
                for (int i = arraysize(kVersionSizes) - 1; i >= 0; --i) {
                    if (object->header_.version >= kVersionSizes[i].version) {
                        if (object->header_.num_bytes == kVersionSizes[i].num_bytes)
                            break;

                        ReportValidationError(
                            validation_context,
                            mojo::internal::VALIDATION_ERROR_UNEXPECTED_STRUCT_HEADER);
                        return false;
                    }
                }
            } else if (object->header_.num_bytes < kVersionSizes[arraysize(kVersionSizes) - 1].num_bytes) {
                ReportValidationError(
                    validation_context,
                    mojo::internal::VALIDATION_ERROR_UNEXPECTED_STRUCT_HEADER);
                return false;
            }

            if (!mojo::internal::ValidatePointerNonNullable(
                    object->src, "null src field in MediaImage",
                    validation_context)) {
                return false;
            }
            if (!mojo::internal::ValidateStruct(object->src, validation_context))
                return false;

            if (!mojo::internal::ValidatePointerNonNullable(
                    object->type, "null type field in MediaImage",
                    validation_context)) {
                return false;
            }
            if (!mojo::internal::ValidateStruct(object->type, validation_context))
                return false;

            if (!mojo::internal::ValidatePointerNonNullable(
                    object->sizes, "null sizes field in MediaImage",
                    validation_context)) {
                return false;
            }
            const mojo::internal::ContainerValidateParams sizes_validate_params(
                0, false, nullptr);
            if (!mojo::internal::ValidateContainer(object->sizes, validation_context,
                    &sizes_validate_params)) {
                return false;
            }

            return true;
        }

